var header=document.querySelector(".hero");function animateHeader(){if(header){if(window.pageYOffset>0)return void header.classList.add("_active");header.classList.remove("_active")}}window.addEventListener("scroll",animateHeader);var scroll=document.querySelector(".scrolldown");function animateScroll(){if(scroll){if(window.pageYOffset>0)return void scroll.classList.add("_active-scroll");scroll.classList.remove("_active-scroll")}}window.addEventListener("scroll",animateScroll);